mapper.select:
SELECT 

FROM oa_leave a

WHERE a.id = #{id}

SELECT 

FROM oa_leave a

WHERE a.del_flag = #{DEL_FLAG_NORMAL} 

	AND a.create_date >= #{createDateStart}


	AND a.create_date <= #{createDateEnd}


	AND a.leave_type = #{leaveType}


	AND a.id IN (${ids})

${sqlMap.dsf}
ORDER BY a.id DESC

mapper.update:
UPDATE oa_leave SET 
	process_instance_id = #{processInstanceId}
WHERE id = #{id}

UPDATE oa_leave SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
